mapper.select:

  	 	select count(*)
		from project
		where deadline > now() and work_field = #{work_field}

  	 	select proj_no
		from bookmark
		where free_no = #{free_no}


  	 	select count(*)
		from freelancer
		where work_field = #{work_field}

  	 	select free_no
		from bookmark
		where ct_no = #{ct_no}
